import React, { Component } from "react";
import DiscoverBlock from "./DiscoverBlock/components/DiscoverBlock";
import "../styles/_discover.scss";
import DiscoverService from "../../../services/discover";
import ReactLoading from "react-loading";

export default class Discover extends Component {
  constructor() {
    super();
    this.state = {
      newReleases: [],
      playlists: [],
      categories: [],
      loading: true,
    };
  }
  componentDidMount() {
    this.getAPI();
  }
  getAPI() {
    try {
      Promise.allSettled([
        DiscoverService.getNewReleased(),
        DiscoverService.getFeaturedPlaylists(),
        DiscoverService.getCategories(),
      ]).then(([newReleased, featuredPlaylists, category]) => {
        this.setState({
          newReleases: newReleased.value.albums.items,
          playlists: featuredPlaylists.value.playlists.items,
          categories: category.value.categories.items,
          loading: false,
        });
      });
    } catch (error) {
      this.setState({ hasError: true });
    }
  }

  render() {
    const { newReleases, playlists, categories, loading } = this.state;

    return (
      <div className="discover">
        {loading ? (
          <ReactLoading
            color="#564FD8"
            type="spinningBubbles"
            height="20%"
            width="20%"
            className="loading"
          />
        ) : (
          <>
            {" "}
            <DiscoverBlock
              text="RELEASED THIS WEEK"
              id="released"
              data={newReleases}
            />
            <DiscoverBlock
              text="FEATURED PLAYLISTS"
              id="featured"
              data={playlists}
            />
            <DiscoverBlock
              text="BROWSE"
              id="browse"
              data={categories}
              imagesKey="icons"
            />
          </>
        )}
      </div>
    );
  }
}
