import Axios from "axios";
// import { getAccessToken } from '../services/auth';
import config from "../config";

export const bati = Axios.create({
  baseURL: `${config.api.baseUrl}`,
});

const getAccessToken = async () => {
  let auth = `${config.api.clientId}:${config.api.clientSecret}`;
  let encodedAuth = btoa(auth);
    let initBody = new URLSearchParams();
    initBody.set("grant_type", "client_credentials");

  console.log(encodedAuth);
  const data = await Axios({
    method: 'post',
    url:'https://accounts.spotify.com/api/token',
    params: {
        grant_type: "client_credentials"
    },
    headers: {
        "Accept": "application/json",
        "Content-Type": "application/x-www-form-urlencoded"
    },
    auth: {
        username: `${config.api.clientId}`,
        password: `${config.api.clientSecret}`
    }
})
    .then((token) => {
      return token;
    })
    .catch((e) => {
      return e.response.data;
    });
  console.log(data);
  console.log("asd");
  return data ? data : null;
};

bati.interceptors.request.use(async (config) => {
  const token = await getAccessToken();
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});
