import React, { Component, useEffect, useState } from "react";
import DiscoverBlock from "./DiscoverBlock/components/DiscoverBlock";
import "../styles/_discover.scss";
import DiscoverService from "../../../services/discover";
import { useSelector } from "react-redux";

function Discover() {
  const user = useSelector(selectUser);
  const [data, setData] = useState({
    user: "",
    newReleased: [],
    playlists: [],
    categories: [],
  });

  useEffect(() => {
    try {
      Promise.allSettled([
        DiscoverService.getNewReleased(),
        DiscoverService.getFeaturedPlaylists(),
        DiscoverService.getCategories(),
      ]).then(([newReleased, featuredPlaylists, category]) => {
        console.log(newReleased);
        this.setData({
          newReleases: newReleased.value.albums.items,
          playlists: featuredPlaylists.value.playlists.items,
          categories: category.value.categories.items,
        });
      });
    } catch (error) {
      this.setState({ hasError: true });
    }
  }, []);

  return (
    <div className="discover">
      <DiscoverBlock
        text="RELEASED THIS WEEK"
        id="released"
        data={data.newReleases}
      />
      <DiscoverBlock text="FEATURED PLAYLISTS" id="featured" data={data.playlists} />
      <DiscoverBlock
        text="BROWSE"
        id="browse"
        data={data.categories}
        imagesKey="icons"
      />
    </div>
  );
}

export default Discover;

