import Axios from "axios";
import { useSelector } from "react-redux";
// import { getAccessToken } from '../services/auth';
import config from "../config";
import { selectUser } from "../features/userSlice";

export const bati = Axios.create({
  baseURL: `${config.api.baseUrl}`,
});

export const auth = async () => {

  let auth = `${config.api.clientId}:${config.api.clientSecret}`;
  let encodedAuth = btoa(auth);
  const data = await Axios({
    method: "post",
    url: "https://accounts.spotify.com/api/token",
    headers: {
      "Content-Type": "application/x-www-form-urlencoded",
      Authorization: `Basic ${encodedAuth}`,
    },
    data: "grant_type=client_credentials",
  })
    .then((res) => {
      //   return res.data.access_token;
      return res.data;
    })
    .catch((e) => {
      return e.response.data;
    });

  //   if (typeof document !== "undefined") {
  //     console.log(accessToken);
  //     document.cookie = `accessToken=${accessToken}; path=/`;
  //   }
  //   return accessToken ? accessToken : null;
  return data ? data : null;
};

bati.interceptors.request.use(async (config) => {
    const user = useSelector(selectUser);
//   let appSpotify = window.__APP_SPOTIFY__;
//   console.log(appSpotify)
  //   const token = await getAccessToken();
  const token = user.access_token;
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});
